import { createEffect } from "effector";

import api from "../../api";

export const postLogin = createEffect(
  async (params: { name: string; login: string; password: string }) => {
    const uuid = await api.postRegister(params);
    localStorage.setItem("uuid", uuid);

    return uuid;
  }
);
